sql.trim:
AND T.APP_CODE=#{vo.appCode,jdbcType=VARCHAR}

	AND T.LOG_MODULE=#{vo.logModule,jdbcType=VARCHAR}


	AND T.LOG_MESSAGE LIKE CONCAT('%',TRIM(#{vo.logMessage,jdbcType=VARCHAR}),'%')


	AND T.LOG_METHOD LIKE CONCAT('%',TRIM(#{vo.logMethod,jdbcType=VARCHAR}),'%')


	AND T.LOG_IP LIKE CONCAT('%',TRIM(#{vo.logIp,jdbcType=VARCHAR}),'%')


	AND T.LOG_TYPE = #{vo.logType,jdbcType=VARCHAR}


	AND T.USER_ID = #{vo.userId,jdbcType=NUMERIC}


	AND T.LOG_TIME BETWEEN #{vo.logTime[0]} AND #{vo.logTime[1]}


	AND T.LOG_START_TIME BETWEEN #{vo.logStartTime[0]} AND #{vo.logStartTime[1]}


	AND T.LOG_END_TIME BETWEEN #{vo.logEndTime[0]} AND #{vo.logEndTime[1]}
