mapper.select:
SELECT T.USER_ID AS userId, T.USER_NO AS userNo, T.USER_CN AS userCn, T.USER_EN AS userEn, T.USER_TYPE AS userType, T.USER_STATUS as userStatus, D.USER_SEX AS userSex, D.USER_PHOTO AS userPhoto FROM SYS_USER_T T LEFT JOIN SYS_USER_DETAIL_T D ON D.USER_ID=T.USER_ID
#{item,jdbcType=NUMERIC}
