sql.trim:
		    T.ENABLE_FLAG='Y'

                AND T.USER_NO LIKE CONCAT('%',TRIM(#{vo.userNo,jdbcType=VARCHAR}),'%')


				AND T.USER_CN LIKE CONCAT('%',TRIM(#{vo.userCn,jdbcType=VARCHAR}),'%')


				AND T.USER_EN LIKE CONCAT('%',TRIM(#{vo.userEn,jdbcType=VARCHAR}),'%')


                AND T.MOBILE LIKE CONCAT('%',TRIM(#{vo.mobile,jdbcType=VARCHAR}),'%')


				AND T.EMAIL LIKE CONCAT('%',TRIM(#{vo.email,jdbcType=VARCHAR}),'%')


				AND T.USER_TYPE=#{vo.userType,jdbcType=VARCHAR}


				AND T.USER_SEX=#{vo.userSex,jdbcType=VARCHAR}


				AND T.USER_STATUS=#{vo.userStatus,jdbcType=VARCHAR}


				AND T.MOBILE_VALIDATED=#{vo.mobileValidated,jdbcType=VARCHAR}


				AND T.EMAIL_VALIDATED=#{vo.emailValidated,jdbcType=VARCHAR}

mapper.update:
UPDATE SYS_USER_T T SET
	T.PASSWORD_ERROR_NUM=#{vo.passwordErrorNum,jdbcType=NUMERIC},
    T.USER_STATUS=#{vo.userStatus,jdbcType=VARCHAR},
    T.LOCK_TIME=#{vo.lockTime}
WHERE T.USER_ID=#{vo.userId,jdbcType=NUMERIC}

mapper.select:
SELECT T.USER_ID, T.USER_NO, T.USER_CN, T.USER_EN, T.MOBILE, T.EMAIL, T.USER_TYPE, T.USER_STATUS, T.USER_SEX, T.USER_PHOTO FROM SYS_USER_T T WHERE T.ENABLE_FLAG='Y'
AND T.USER_TYPE=#{vo.userType,jdbcType=VARCHAR}
AND T.USER_STATUS=#{vo.userStatus,jdbcType=VARCHAR}
AND T.USER_SEX=#{vo.userSex,jdbcType=VARCHAR}
AND (T.USER_CN LIKE CONCAT('%',TRIM(#{vo.keyword,jdbcType=VARCHAR}),'%') OR T.USER_EN LIKE CONCAT('%',TRIM(#{vo.keyword,jdbcType=VARCHAR}),'%') OR T.USER_NO LIKE CONCAT('%',TRIM(#{vo.keyword,jdbcType=VARCHAR}),'%') )
LIMIT 50
